INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (1, 'Modernes Ferienhaus mit Garten & Terrasse, 300m zum Strand', 'Die Ferienhaus \'Tu Hus in Linde Eck\' steht in zweiter Reihe und ist ruhig gelegen. Bei einer Größe von 55 m², bietet es Platz für drei Personen. Ein Wohnzimmer, 2 Schlafzimmer (Doppelbett und Einzelbett) sowie Küche, Bad/WC und eine Südterrasse. Der Parkplatz befindet sich direkt am Haus.
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (1, 'Blumenstrauß', 'Sie erhalten einen schönen Blumenstrauß, der bei Ihrer Ankunft im Haus auf Sie wartet. Eine romantische Überraschung für Ihren Partner oder Ihre Partnerin.', 15, 0, 1, 6);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (2, 'Wellness-Paket', 'Wellness-Paket in einem Wellness-Center. Sie erhalten eine Auswahl an Behandlungen, wie z.B. eine Gesichtsbehandlung, eine Maniküre, eine Pediküre, eine Massage oder eine Aromatherapie. Das Wellness-Center ist modern und sauber und verfügt über einen Pool, eine Sauna und einen Fitnessraum. Das Wellness-Paket dauert etwa 2,5 Stunden und findet jeden Nachmittag um 14 Uhr statt. Ein Shuttlebus bringt Sie hin und zurück.', 50, 0, 1, 7);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (3, 'Massage', 'Eine professionelle Masseurin kommt zu Ihnen ins Haus und bietet Ihnen eine entspannende Massage an. Die Massage ist individuell auf Ihre Bedürfnisse abgestimmt und dauert etwa eine Stunde. Die Massage findet jeden Tag um 16 Uhr im Schlafzimmer des Hauses statt. Sie erhalten eine Liege, ein Handtuch und ein Massageöl.', 20, 0, 1, 8);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (4, 'Fahrradtour', 'Ein erfahrener Fahrradführer zeigt Ihnen die schönsten Orte und Landschaften in der Umgebung. Die Fahrradtour ist für alle Schwierigkeitsgrade geeignet und dauert etwa 3 Stunden. Die Fahrradtour startet jeden Tag um 10 Uhr vor dem Haus. Sie erhalten ein Fahrrad, einen Helm und eine Trinkflasche.', 15, 0, 1, 9);
