INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (5, 'Ohrstöpsel', 'Ohrstöpsel, die Sie benutzen können, um den Lärm etwas zu dämpfen. Die Ohrstöpsel sind aus Schaumstoff und passen sich Ihrem Ohr an. Die Ohrstöpsel liegen im Schlafzimmer auf dem Kopfkissen.', 2, 0, 2, 15);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (6, 'Reinigungsmittel', 'Ein Set von Reinigungsmitteln, die Sie benutzen können, um das Haus etwas sauberer zu machen. Die Reinigungsmittel bestehen aus einem Schwamm, einem Lappen, einem Besen, einem Eimer und einer Flasche Allzweckreiniger. Die Reinigungsmittel liegen im Badezimmer unter dem Waschbecken.', 9, 0, 2, 16);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (7, 'Taxi', 'Ein Taxi, das Sie zum nächsten Bahnhof oder Flughafen bringt. Ein Taxi ist ein Auto mit einem Fahrer, der Sie abholt und zu Ihrem Ziel bringt. Das Taxi ist schnell und bequem und spart Ihnen Zeit und Nerven. Das Taxi ist jeden Tag von 8 Uhr bis 20 Uhr verfügbar und kann an der Rezeption des Hauses bestellt werden.', 15, 0, 2, 17);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (8, 'Grillabend', 'Ein professioneller Grillmeister bereitet für Sie ein leckeres Grillmenü zu. Sie erhalten eine Auswahl an Fleisch, Wurst, Gemüse und Salat. Der Grillabend findet jeden Abend um 18 Uhr im Garten des Hauses statt. Sie erhalten einen Grill, eine Schürze und ein Besteck.', 25, 0, 2, 18);
