INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (3, 'Wellnessresidenz Störtebeker WG 9 Nähe zum Strand', 'Exklusiv eingerichtete Ferienwohnug über zwei Etagen (49qm) für 2 Personen. Die Wohnung verfügt über 1 Wohn- und Essraum mit anschließendem Südbalkon und hochwertiger Küchenzeile (inkl. Geschirrspüler), 1 Schlafzimmer mit Doppelbett, 1 geräumiges Badezimmer mit Dusche/WC und anschließendem Südbalkon sowie allem Komfort wie Fußbodenheizung, Fernseher in Wohn- und Schlafzimmer und die Nutzung des hauseigenen Wellnessbereichs. (Bitte die dann vor Ort geltenden Öffnungszeiten beachten)
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (9, 'Garage', 'Hier steht ihr Auto immer im Trockenen und sie müssen sich keine Sorgen um Witterung machen', 99, 0, 3, 24);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (10, 'Spielplatz', 'Kein Bock mehr auf ihre Kinder, sperren Sie sie einfach für einen Tag auf dem Spielplatz ein. Durch abgelegene Lage werden weder Sie noch ihre Nachbarn durch lautes Schreien oder ähnliches gestört.', 25, 0, 3, 25);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (11, 'Sauna', 'Nehmen Sie die Sauna überall mit hin wo sie hinfahren', 10, 0, 3, 26);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (12, 'Fitness', 'Anabolika vergessen? Kein Problem, hier können Sie auch mal auf die altmodische art Trainieren.', 9, 0, 3, 27);
