INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (14, 'Sauna', 'Die Sauna ist aus Holz gebaut und hat eine angenehme Temperatur. Sie liegt im Keller des Hauses und ist mit Handtüchern und einem Wasserkocher ausgestattet.', 10, 0, 4, 34);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (15, 'Weinprobe', 'Ein erfahrener Sommelier zeigt Ihnen die verschiedenen Rebsorten und Geschmacksrichtungen. Die Weinprobe findet jeden Abend um 18 Uhr im Weinkeller des Hauses statt. Sie erhalten ein Weinglas und eine Käseplatte.', 20, 0, 4, 35);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (16, 'Kochkurs', 'Ein professioneller Koch zeigt Ihnen, wie Sie typische Gerichte zubereiten und welche Zutaten Sie verwenden. Der Kochkurs dauert etwa 2 Stunden und findet jeden Nachmittag um 16 Uhr in der Küche des Hauses statt. Sie erhalten eine Schürze, ein Kochbuch und eine Portion des gekochten Essens.', 15, 0, 4, 36);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (17, 'Kamin', 'Der Kamin ist aus Stein gebaut und hat eine gemütliche Atmosphäre. Er liegt im Wohnzimmer des Hauses und ist mit Holz und Streichhölzern ausgestattet.', 10, 0, 4, 37);
