INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (6, 'Romantisches Fachwerkhaus im Grünen mit Kamin und Garten', 'Das Ferienhaus für Paare, die eine Auszeit vom Alltag suchen. Das Haus ist ein liebevoll restauriertes Fachwerkhaus aus dem 18. Jahrhundert und verfügt über einen gemütlichen Kamin im Wohnzimmer, eine voll ausgestattete Küche, ein Schlafzimmer mit Doppelbett und ein Badezimmer mit Badewanne. Das Haus liegt in einem idyllischen Dorf im Grünen, wo Sie die Ruhe und die Natur genießen können. Der Garten bietet viel Platz zum Entspannen, Grillen oder Spielen. Die Umgebung lädt zu Spaziergängen, Fahrradtouren oder Besuchen von Burgen und Klöstern ein. Das Ferienhaus ist geschmackvoll eingerichtet und bietet Platz für bis zu 2 Personen.', 100, 2, '20095', 'Hamburg', 'Am Kaiserkai', 7, 60, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (21, 'Frühstückskorb', 'Sie erhalten einen Korb mit frischen Brötchen, Marmelade, Käse, Wurst, Eiern, Obst und Saft, der jeden Morgen an Ihre Tür geliefert wird', 10, 0, 6, 51);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (22, 'Massage', 'Gönnen Sie sich eine entspannende Massage im Haus oder im Garten. Ein professioneller Masseur kommt zu Ihnen und bringt alles mit, was Sie brauchen', 30, 0, 6, 52);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (23, 'Kutschfahrt', 'Buchen Sie eine Kutschfahrt durch die malerische Landschaft. Ein erfahrener Kutscher zeigt Ihnen die schönsten Orte und erzählt Ihnen interessante Geschichten. Die Kutschfahrt dauert etwa eine Stunde und startet jeden Tag um 11 Uhr vor dem Haus.', 20, 0, 6, 53);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (24, 'Candle-Light-Dinner', 'Genießen Sie ein Candle-Light-Dinner in einem nahegelegenen Restaurant. Sie erhalten ein 3-Gänge-Menü mit regionalen Spezialitäten und einem Glas Wein. Das Restaurant ist gemütlich und stilvoll eingerichtet und bietet eine angenehme Atmosphäre. Das Candle-Light-Dinner findet jeden Abend um 19 Uhr statt. Ein Taxi bringt Sie hin und zurück.', 30, 0, 6, 54);
