INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (7, 'Geräumiges Chalet in den Alpen mit Kamin und Balkon', 'Ideales Ferienhaus für Familien oder Gruppen, die einen aktiven und abwechslungsreichen Urlaub in den Bergen verbringen möchten. Das Haus ist ein geräumiges Chalet aus Holz und Stein und verfügt über einen großen Kamin im Wohnzimmer, eine gut ausgestattete Küche, vier Schlafzimmer mit Doppelbetten und zwei Badezimmer mit Dusche und Badewanne. Das Highlight ist der Balkon mit einem herrlichen Blick auf die schneebedeckten Gipfel der Alpen. Das Haus liegt in einem kleinen Dorf in den Alpen, wo Sie viele Möglichkeiten für Ski-, Wander-, Rad- oder Klettertouren haben. Das Ferienhaus ist gemütlich und rustikal eingerichtet und bietet Platz für bis zu 8 Personen.', 200, 8, '82481', 'Mittenwald', 'Alpenstraße', 23, 120, 5, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (25, 'Skipass', 'Für 25 € pro Person und Tag erhalten Sie einen Skipass, mit dem Sie alle Skilifte und Pisten in der Umgebung nutzen können. Es gibt verschiedene Schwierigkeitsgrade und Längen für Anfänger und Fortgeschrittene. Die Skilifte sind nur wenige Minuten vom Haus entfernt.', 25, 0, 7, 60);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (26, 'Schlittenverleih', 'Für 10 € pro Tag können Sie einen Schlitten ausleihen und die Rodelbahn hinunterfahren. Eine lustige Aktivität für Jung und Alt. Die Rodelbahn ist etwa 2 km lang und startet am Ende der Straße.', 10, 0, 7, 61);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (27, 'Fondue-Abend', 'Für 20 € pro Person können Sie einen Fondue-Abend im Haus genießen. Sie erhalten einen Topf mit geschmolzenem Käse, Brotwürfel, Kartoffeln, Gemüse und Obst, die Sie in den Käse tauchen können. Ein leckeres und geselliges Essen für kalte Winterabende.', 20, 0, 7, 62);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (28, 'Schneeschuhwanderung', 'Für 15 € pro Person können Sie an einer geführten Schneeschuhwanderung durch die verschneite Landschaft teilnehmen. Ein erfahrener Guide zeigt Ihnen die schönsten Orte und erzählt Ihnen Wissenswertes über die Natur und die Kultur der Region. Die Schneeschuhwanderung dauert etwa 2 Stunden und startet jeden Tag um 10 Uhr vor dem Haus.', 15, 0, 7, 63);
