INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (9, 'Modernes Loft in der Innenstadt mit Dachterrasse und Whirlpool', 'Das perfekte Ferienhaus für Stadtliebhaber, die Komfort und Luxus schätzen. Das Haus ist ein umgebautes Loft in einem historischen Gebäude und verfügt über eine offene Küche, ein Wohnzimmer mit Flachbild-TV und Soundsystem, ein Schlafzimmer mit Kingsize-Bett und ein Badezimmer mit Regendusche. Das Highlight ist die Dachterrasse mit einem Whirlpool, einer Lounge-Ecke und einem atemberaubenden Blick über die Stadt. Das Haus liegt im Zentrum der Stadt, wo Sie viele Geschäfte, Restaurants, Bars, Museen und Sehenswürdigkeiten finden. Das Ferienhaus ist stilvoll eingerichtet und bietet Platz für bis zu 4 Personen.', 200, 4, '20098', 'Hamburg', 'Am Sandtorkai', 85, 100, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (31, 'Stadtführung', 'Eine geführten Tour durch die Stadt um mehr über die Geschichte, Kultur und Architektur zu erfahren. Die Tour dauert etwa 2 Stunden und startet jeden Tag um 10 Uhr vor dem Rathaus', 20, 0, 9, 75);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (32, 'Weinprobe', ' Nehmen Sie an einer Weinprobe in einem renommierten Weingut teil und probieren Sie verschiedene Sorten von lokalen Winzern. Die Weinprobe dauert etwa 1,5 Stunden und findet jeden Abend um 18 Uhr statt. Ein Shuttlebus bringt Sie hin und zurück.', 25, 0, 9, 76);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (33, 'Kochkurs', 'Sie können an einem Kochkurs in einer professionellen Küche teilnehmen und lernen, wie man typische Gerichte der Region zubereitet. Der Kochkurs dauert etwa 3 Stunden und findet jeden Nachmittag um 15 Uhr statt. Sie können das Essen anschließend mit den anderen Teilnehmern genießen.', 30, 0, 9, 77);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (34, 'Theaterkarten', 'Karten für eine Vorstellung in einem der besten Theater der Stadt. Es gibt verschiedene Genres und Stücke zur Auswahl, von Komödie über Drama bis hin zu Musical. Die Vorstellungen beginnen jeden Abend um 20 Uhr. Sie erhalten die Karten an der Rezeption des Hauses.', 40, 0, 9, 78);
