INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (10, 'Elegantes Penthouse in der City mit Balkon und Parkplatz', 'Dieses Ferienhaus ist perfekt für Geschäftsreisende oder Städtereisende, die Komfort und Eleganz schätzen. Das Haus ist ein luxuriöses Penthouse in einem modernen Gebäude und verfügt über eine offene Küche, ein Wohnzimmer mit Ledersofa und Smart-TV, ein Schlafzimmer mit Queensize-Bett und ein Badezimmer mit Marmorfliesen. Das Highlight ist der Balkon mit einem kleinen Tisch und zwei Stühlen, von dem aus Sie einen herrlichen Blick über die City haben. Das Haus liegt im Herzen der Stadt, wo Sie viele Einkaufsmöglichkeiten, Restaurants, Cafés, Clubs, Theater und Museen finden. Das Ferienhaus ist elegant eingerichtet und bietet Platz für bis zu 2 Personen.', 150, 2, '20097', 'Hamburg', 'Singapurstraße', 54, 70, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (35, 'Minibar', 'Erhalten Sie Zugang zu einer Minibar, die mit verschiedenen Getränken und Snacks gefüllt ist. Sie können sich jederzeit bedienen und müssen nichts extra bezahlen.', 100, 0, 10, 84);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (36, 'Wäscheservice', 'Nehmen Sie den Wäscheservice in Anspruch. Sie können Ihre Kleidung in einen Korb legen und an der Rezeption abgeben. Sie erhalten Ihre Kleidung am nächsten Tag frisch gewaschen, gebügelt und gefaltet zurück.', 78, 0, 10, 85);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (37, 'Business-Paket', 'Das Business-Paket buchen, welches Ihnen einen schnellen Internetzugang, einen Drucker, einen Scanner, einen Kopierer und ein Faxgerät zur Verfügung stellt. Sie können diese Geräte in einem separaten Raum im Haus nutzen oder an Ihr Laptop anschließen.', 120, 0, 10, 86);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (38, 'Konzertkarten', 'Karten für ein Konzert in einer der besten Konzerthallen der Stadt kaufen. Es gibt verschiedene Genres und Künstler zur Auswahl, von Rock über Pop bis hin zu Klassik. Die Konzerte beginnen jeden Abend um 20 Uhr. Sie erhalten die Karten an der Rezeption des Hauses.', 49, 0, 10, 87);
