INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (11, 'Helles Strandhaus an der Nordsee mit Terrasse und Garten', 'Dieses Ferienhaus ist ideal für Urlauber, die Sonne, Sand und Meer lieben. Das Haus ist ein helles Strandhaus aus Holz und Glas und verfügt über eine offene Küche, ein Wohnzimmer mit Esstisch und Sofa, zwei Schlafzimmer mit Einzelbetten und ein Badezimmer mit Dusche. Das Highlight ist die Terrasse mit einem großen Tisch und vier Stühlen, von der aus Sie einen direkten Zugang zum Garten und zum Strand haben. Das Haus liegt an der Nordsee, wo Sie das Rauschen der Wellen, die frische Brise und die weiten Dünen genießen können. Die Umgebung bietet viele Möglichkeiten für Wassersport, Strandspaziergänge oder Ausflüge zu Inseln und Küstenstädten. Das Ferienhaus ist modern und freundlich eingerichtet und bietet Platz für bis zu 4 Personen.', 120, 4, '20097', 'Hamburg', 'Marktstraße', 10, 70, 3, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (39, 'Strandkorb', 'Strandkorb mieten und es sich darin gemütlich machen. Ein Strandkorb ist ein typisch deutscher Korb mit zwei Sitzen, einem Dach und einer Fußstütze, der Sie vor Wind und Sonne schützt. Der Strandkorb steht direkt vor dem Haus am Strand', 5, 0, 11, 94);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (40, 'Surfkurs', 'An einem Surfkurs teilnehmen und das Wellenreiten lernen. Ein erfahrener Surflehrer zeigt Ihnen die Grundlagen und hilft Ihnen bei den ersten Versuchen. Der Surfkurs dauert etwa 2 Stunden und findet jeden Tag um 10 Uhr am Strand statt. Sie erhalten eine Surfausrüstung mit Brett, Anzug und Helm.', 20, 0, 11, 95);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (41, 'Fischbuffet', 'Fischbuffet in einem lokalen Restaurant. Sie erhalten eine Auswahl an frischen Fischgerichten, wie z.B. Matjes, Krabben, Lachs, Scholle, Hering und mehr. Dazu gibt es verschiedene Beilagen, wie z.B. Kartoffeln, Salat, Brot und Butter. Das Restaurant ist urig und gemütlich eingerichtet und bietet einen schönen Blick auf das Meer. Das Fischbuffet findet jeden Abend um 18 Uhr statt. Ein Fahrrad bringt Sie hin und zurück.', 15, 0, 11, 96);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (42, 'Wattwanderung', 'Nehmen Sie an einer Wattwanderung tiel um das einzigartige Ökosystem des Wattenmeeres zu erkunden. Ein erfahrener Wattführer zeigt Ihnen die verschiedenen Tiere und Pflanzen, die im Schlick und im Wasser leben. Die Wattwanderung dauert etwa 1,5 Stunden und findet jeden Tag um 14 Uhr am Strand statt. Sie benötigen Gummistiefel, eine lange Hose und eine Jacke.', 10, 0, 11, 97);
