INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (12, 'Luxuriöse Villa am Mittelmeer mit Pool und Meerblick', 'Dieses Ferienhaus ist ideal für Urlauber, die Luxus und Exklusivität suchen. Das Haus ist eine luxuriöse Villa aus Stein und Glas und verfügt über eine große Küche, ein Wohnzimmer mit Kamin und Panoramafenster, drei Schlafzimmer mit Kingsize-Betten und Ensuite-Badezimmern mit Whirlpool. Das Highlight ist der Pool mit einer Liegewiese, einer Bar und einem atemberaubenden Blick auf das Mittelmeer. Das Haus liegt an der Mittelmeerküste, wo Sie das warme Klima, die feinen Sandstrände und das kristallklare Wasser genießen können. Die Umgebung bietet viele Möglichkeiten für Wassersport, Golf, Shopping oder Kultur. Das Ferienhaus ist luxuriös und elegant eingerichtet und bietet Platz für bis zu 6 Personen.', 300, 6, '18039', 'Ventimiglia', 'Via del Mare', 10, 150, 4, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (43, 'Limousinenservice', 'Ein Limousinenservice buchen, der Sie zu jedem gewünschten Ort bringt. Sie können aus verschiedenen Modellen und Farben wählen und einen Chauffeur mit Uniform und Handschuhen haben. Der Limousinenservice ist rund um die Uhr verfügbar und kann an der Rezeption des Hauses bestellt werden.', 50, 0, 12, 103);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (44, 'Yachtcharter', 'Chartern Sie eine Yacht um eine Fahrt auf dem See zu machen. Sie können aus verschiedenen Größen und Ausstattungen wählen und einen Kapitän und eine Crew haben. Die Yacht verfügt über eine Küche, eine Lounge, eine Sonnenterrasse und ein Bad. Der Yachtcharter ist jeden Tag von 9 Uhr bis 18 Uhr verfügbar und kann an der Rezeption des Hauses bestellt werden.', 200, 0, 12, 104);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (45, 'Gourmet-Menü', 'Ein Gourmet-Menü in einem Sternerestaurant genießen. Sie erhalten ein 5-Gänge-Menü mit mediterranen Spezialitäten und einem Glas Champagner. Das Restaurant ist edel und raffiniert eingerichtet und bietet einen fantastischen Blick auf das Meer. Das Gourmet-Menü findet jeden Abend um 20 Uhr statt. Ein Taxi bringt Sie hin und zurück.', 40, 0, 12, 105);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (46, 'Helikopterflug', 'Buchen Sie einen Helikopterflug und betrachten Sie die Küste aus der Vogelperspektive. Ein erfahrener Pilot zeigt Ihnen die schönsten Orte und erzählt Ihnen interessante Fakten. Der Helikopterflug dauert etwa 30 Minuten und startet jeden Tag um 12 Uhr am Hubschrauberlandeplatz in der Nähe des Hauses.', 100, 0, 12, 106);
