INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (13, 'Gemütliches Reetdachhaus auf einer Insel mit Kamin und Garten', 'Dieses Ferienhaus ist ideal für Urlauber, die Ruhe und Erholung suchen. Das Haus ist ein gemütliches Reetdachhaus aus dem 19. Jahrhundert und verfügt über eine kleine Küche, ein Wohnzimmer mit Kamin und Bücherregal, ein Schlafzimmer mit Doppelbett und ein Badezimmer mit Dusche. Das Highlight ist der Garten mit einem Teich, einer Hängematte und einem Obstbaum. Das Haus liegt auf einer kleinen Insel in der Nordsee, wo Sie die Abgeschiedenheit und die Natur genießen können. Die Insel ist autofrei und nur mit dem Boot erreichbar. Die Umgebung bietet viele Möglichkeiten für Vogelbeobachtung, Angeln oder Picknick. Das Ferienhaus ist urig und charmant eingerichtet und bietet Platz für bis zu 2 Personen.', 80, 2, '25849', 'Pellworm', 'Inselweg', 3, 50, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (47, 'Bootsfahrt', 'Sie können eine Bootsfahrt um die Insel machen und die schöne Aussicht genießen. Ein freundlicher Bootsführer zeigt Ihnen die verschiedenen Sehenswürdigkeiten und erzählt Ihnen spannende Geschichten. Die Bootsfahrt dauert etwa eine Stunde und startet jeden Tag um 11 Uhr am Hafen.', 60, 0, 13, 112);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (48, 'Kuchen', 'Erhalten Sie einen leckeren Kuchen, der jeden Nachmittag an Ihre Tür geliefert wird. Der Kuchen ist hausgemacht und mit frischen Zutaten aus dem Garten zubereitet. Sie können aus verschiedenen Sorten wählen, wie z.B. Apfel, Kirsche, Schokolade oder Nuss.', 10, 0, 13, 113);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (49, 'Leuchtturmbesichtigung', 'Machen Sie eine Leuchtturmbesichtigung um den höchsten Punkt der Insel zu erreichen. Ein erfahrener Leuchtturmwärter zeigt Ihnen die verschiedenen Räume und Geräte und erzählt Ihnen interessante Fakten. Die Leuchtturmbesichtigung dauert etwa 45 Minuten und findet jeden Tag um 15 Uhr statt. Sie müssen eine Leiter hochsteigen, um den Leuchtturm zu betreten.', 18, 0, 13, 114);
