INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (14, 'Verlassenes Herrenhaus im Wald mit Kamin', 'Dieses Ferienhaus ist nur für Mutige, die einen Nervenkitzel suchen. Das Haus ist ein verlassenes Herrenhaus aus dem 19. Jahrhundert und verfügt über einen alten Kamin im Wohnzimmer, eine verstaubte Küche, ein Schlafzimmer mit einem zerfetzten Himmelbett und ein Badezimmer mit einer rostigen Badewanne. Das Highlight ist der Spuk, der das Haus heimsucht. Das Haus liegt in einem dunklen Wald, wo Sie keine Nachbarn oder Zivilisation finden. Der Wald ist voller Geräusche, Schatten und Gefahren. Die Umgebung bietet keine Möglichkeiten für Freizeitaktivitäten, außer sich zu gruseln. Das Ferienhaus ist schmutzig und unheimlich eingerichtet und bietet Platz für bis zu 2 Personen.', 50, 2, '34567', 'Schreckenbach', 'Waldweg', 13, 100, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (50, 'Ouija-Brett', 'Ouija-Brett, mit dem Sie versuchen können, mit den Geistern im Haus zu kommunizieren. Ein Ouija-Brett ist ein Brett mit Buchstaben, Zahlen und Symbolen, auf dem Sie eine Planchette bewegen, um Antworten zu erhalten. Das Ouija-Brett liegt im Wohnzimmer auf dem Tisch.', 10, 0, 14, 120);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (51, 'Kerzen', 'Ein Set von Kerzen, die Sie im Haus verteilen können, um für etwas Licht zu sorgen. Die Kerzen sind schwarz und riechen nach verbranntem Holz. Die Kerzen liegen im Schlafzimmer auf dem Nachttisch.', 5, 0, 14, 121);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (52, 'Séance', 'Nehmen Sie an einer Séance teil, bei der Sie versuchen, die Geister im Haus zu beschwören. Ein Medium kommt zu Ihnen ins Haus und leitet die Séance an. Sie müssen sich im Kreis um einen Tisch setzen und sich an den Händen halten. Die Séance dauert etwa eine Stunde und findet jeden Abend um 21 Uhr statt. Sie müssen vorher reservieren, da das Medium sehr gefragt ist.', 20, 0, 14, 122);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (53, 'Fluchtweg', 'Ein Fluchtweg, falls Sie das Haus nicht mehr ertragen können. Ein Fluchtweg ist ein versteckter Ausgang, der Sie aus dem Haus führt. Der Fluchtweg befindet sich im Keller hinter einem Regal. Sie müssen einen Schlüssel finden, um ihn zu öffnen.', 30, 0, 14, 123);
