INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (15, 'Rustikale Hafenspelunke mit Kneipe und Hafenblick', 'Dieses Ferienhaus ist nur für Abenteurer, die das raue Leben am Meer erleben wollen. Das Haus ist eine rustikale Hafenspelunke aus dem 17. Jahrhundert und verfügt über eine kleine Küche, ein Wohnzimmer mit einem Holztisch und einem alten Radio, ein Schlafzimmer mit einem Stockbett und ein Badezimmer mit einer Eimerdusche. Das Highlight ist die Kneipe, die sich im Erdgeschoss befindet und wo Sie mit den Einheimischen trinken, singen und Geschichten austauschen können. Die Kneipe ist mit allerlei Nippes aus der Seefahrt dekoriert und bietet einen tollen Blick auf den Hafen. Das Haus liegt in einer Hafenstadt an der Nordsee, wo Sie das maritime Flair, die frischen Fischgerichte und die historischen Schiffe genießen können. Die Umgebung bietet viele Möglichkeiten für Bootsfahrten, Museumsbesuche oder Hafenrundgänge. Das Ferienhaus ist einfach und urig eingerichtet und bietet Platz für bis zu 2 Personen.', 40, 2, '27568', 'Bremerhaven', 'Hafenstraße', 1, 50, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (54, 'Freibier', 'Eine Karte, mit der Sie unbegrenzt Bier in der Kneipe trinken können. Das Bier ist hausgebraut und hat einen kräftigen Geschmack. Das Freibier ist jeden Tag von 18 Uhr bis 22 Uhr verfügbar und kann an der Theke der Kneipe bestellt werden.', 10, 0, 15, 128);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (55, 'Segeltörn', 'Sie können an einem Segeltörn teilnehmen und das Meer hautnah erleben. Ein erfahrener Skipper zeigt Ihnen die Grundlagen des Segelns und nimmt Sie mit auf eine spannende Fahrt. Der Segeltörn dauert etwa 3 Stunden und findet jeden Tag um 10 Uhr am Hafen statt. Sie erhalten eine Segelausrüstung mit Jacke, Hose und Mütze.', 30, 0, 15, 129);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (56, 'Hafenrallye', 'Eine Hafenrallye um die Stadt spielerisch zu erkunden. Eine Hafenrallye ist eine Schnitzeljagd, bei der Sie verschiedene Rätsel lösen und Aufgaben erfüllen müssen. Die Hafenrallye dauert etwa 2 Stunden und startet jeden Tag um 14 Uhr an der Touristeninformation am Hafen. Sie erhalten eine Karte, einen Kompass und einen Rucksack mit verschiedenen Utensilien.', 15, 0, 15, 130);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (57, 'Shanty-Chor', 'Treten Sie einem Shanty-Chor bei und singen Sie traditionelle Seemannslieder. Ein Shanty-Chor ist eine Gruppe von Sängern, die Lieder über das Leben auf See singen. Der Shanty-Chor trifft sich jeden Abend um 20 Uhr in der Kneipe und singt gemeinsam mit einem Akkordeonspieler. Sie erhalten ein Liederbuch mit den Texten und Melodien.', 5, 0, 15, 131);
