INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (16, 'Idyllische Almhütte in den Dolomiten mit Kamin und Almblick', 'Dieses Ferienhaus ist ideal für Urlauber, die Ruhe und Natur suchen. Das Haus ist eine idyllische Almhütte aus Holz und Stein und verfügt über eine kleine Küche, ein Wohnzimmer mit Kamin und Holztisch, ein Schlafzimmer mit Doppelbett und ein Badezimmer mit Dusche. Das Highlight ist der Almblick, der Ihnen einen herrlichen Blick auf die grünen Wiesen, die bunten Blumen und die majestätischen Gipfel der Dolomiten bietet. Das Haus liegt auf einer Alm in den Dolomiten, wo Sie die frische Luft, die klare Quelle und die gesunden Produkte der Almbauern genießen können. Die Umgebung bietet viele Möglichkeiten für Wanderungen, Radtouren oder Klettertouren. Das Ferienhaus ist einfach und gemütlich eingerichtet und bietet Platz für bis zu 2 Personen.', 60, 2, '39030', 'Prags', 'Almweg', 5, 40, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (58, 'Almkäse', 'Einen Laib Almkäse, der aus der frischen Milch der Almkühe hergestellt wird. Der Almkäse ist würzig und aromatisch und schmeckt am besten mit Brot und Butter. Der Almkäse liegt in der Küche im Kühlschrank.', 5, 0, 16, 137);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (59, 'Almwanderung', 'Sie können an einer Almwanderung teilnehmen und die Schönheit der Almlandschaft entdecken. Ein erfahrener Almführer zeigt Ihnen die verschiedenen Pflanzen und Tiere, die auf der Alm leben. Die Almwanderung dauert etwa 2 Stunden und startet jeden Tag um 10 Uhr vor dem Haus. Sie erhalten eine Wanderkarte und einen Rucksack mit einem Almvesper.', 10, 0, 16, 138);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (60, 'Almjause', 'Eine Almjause in einer Almhütte genießen. Sie erhalten eine Auswahl an typischen Almspezialitäten, wie z.B. Speck, Wurst, Brot, Butter, Marmelade, Kuchen und Schnaps. Die Almhütte ist urig und gemütlich eingerichtet und bietet einen tollen Blick auf die Alm. Die Almjause findet jeden Nachmittag um 16 Uhr statt. Ein Traktor bringt Sie hin und zurück.', 15, 0, 16, 139);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (61, 'Almmusik', 'Almmusik lauschen und traditionelle Volkslieder hören. Eine Almmusik ist eine Gruppe von Musikanten, die mit verschiedenen Instrumenten, wie z.B. Zither, Harfe, Akkordeon oder Flöte, die Alm zum Klingen bringen. Die Almmusik spielt jeden Abend um 20 Uhr in der Kneipe und singt gemeinsam mit den Gästen. Sie erhalten ein Liederbuch mit den Texten und Melodien.', 5, 0, 16, 140);
