INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (17, 'Apartment in der Rigaer Straße mit Graffiti und Ghettoflair', 'Dieses Ferienhaus ist nur für Rebellen, die das alternative Leben in der Stadt suchen. Das Haus ist ein einfaches Apartment in einem besetzten Gebäude und verfügt über eine kleine Küche, ein Wohnzimmer mit einem Sofa und einem Plattenspieler, ein Schlafzimmer mit einem Matratzenlager und ein Badezimmer mit einer Gemeinschaftsdusche. Das Highlight ist das Graffiti, das die Wände des Hauses schmückt und die politischen Botschaften der Bewohner ausdrückt. Das Haus liegt in der Rigaer Straße, wo Sie viel Protest, Widerstand und Solidarität finden. Die Straße ist seit Jahren ein Brennpunkt der Gewalt zwischen Autonomen und der Polizei. Die Umgebung bietet viele Möglichkeiten für Demonstrationen, Konzerte oder Kunst. Das Ferienhaus ist billig und kreativ eingerichtet und bietet Platz für bis zu 4 Personen.', 30, 4, '10247', 'Berlin', 'Rigaer Straße', 94, 60, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (62, 'Spraydose', 'Eine Spraydose, mit der Sie Ihr eigenes Graffiti an die Wand sprühen können. Die Spraydose ist in verschiedenen Farben erhältlich und hat eine hohe Deckkraft. Die Spraydose liegt im Wohnzimmer auf dem Regal.', 5, 0, 17, 146);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (63, 'Konzertkarte', 'Konzertkarte, mit der Sie ein Konzert in einem der vielen alternativen Clubs in der Nähe besuchen können. Das Konzert ist von lokalen Bands, die verschiedene Musikrichtungen spielen, wie z.B. Punk, Rock, Hip-Hop oder Reggae. Das Konzert findet jeden Abend um 21 Uhr statt. Sie erhalten eine Liste mit den aktuellen Terminen und Orten.', 10, 0, 17, 147);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (64, 'Molotowcocktail', 'Gebrauchtes Molotowcocktail, mit dem Sie sich an den Auseinandersetzungen mit der Polizei beteiligen können. Ein Molotowcocktail ist eine Flasche mit brennbarer Flüssigkeit, die mit einem Lappen versehen ist, der als Zündschnur dient. Der Molotowcocktail ist eine gefährliche Waffe und sollte nur im Notfall verwendet werden. Der Molotowcocktail liegt im Schlafzimmer unter dem Bett.', 20, 0, 17, 148);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (65, 'Stadtführung', 'An einer Stadtführung teilnehmen, die Ihnen die Geschichte und die Kultur der Rigaer Straße und des Berliner Ghettos zeigt. Ein erfahrener Stadtführer zeigt Ihnen die verschiedenen Orte und erzählt Ihnen spannende Geschichten. Die Stadtführung dauert etwa 2 Stunden und startet jeden Tag um 14 Uhr vor dem Haus. Sie erhalten eine Broschüre mit den wichtigsten Informationen.', 15, 0, 17, 149);
