INSERT INTO vacation_rental_db.houses (id, name, description, price, max_person, postal_code, city, street, house_number, square_meter, room_count, is_disabled, owner_id) VALUES (18, 'Modernes Loft in der Reeperbahn mit Dachterrasse und Nachtleben', 'Dieses Ferienhaus ist ideal für Urlauber, die Spaß und Unterhaltung suchen. Das Haus ist ein modernes Loft in einem renovierten Gebäude und verfügt über eine offene Küche, ein Wohnzimmer mit einem großen Sofa und einem Flachbild-TV, ein Schlafzimmer mit einem Doppelbett und einem begehbaren Kleiderschrank und ein Badezimmer mit einer Regendusche. Das Highlight ist die Dachterrasse mit einem Whirlpool, einer Lounge und einem fantastischen Blick auf die Reeperbahn. Das Haus liegt in der Reeperbahn, wo Sie das pulsierende Nachtleben, die vielfältigen Bars und Clubs und die berühmten Shows und Musicals genießen können. Die Umgebung bietet viele Möglichkeiten für Shopping, Kultur oder Sightseeing. Das Ferienhaus ist stilvoll und komfortabel eingerichtet und bietet Platz für bis zu 2 Personen.', 150, 2, '20359', 'Hamburg', 'Reeperbahn', 1, 80, 2, 0, 1);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (66, 'Champagner', 'Eine Flasche Champagner, die Sie auf der Dachterrasse genießen können. Der Champagner ist gekühlt und hat einen feinen Geschmack. Der Champagner liegt in der Küche im Kühlschrank.', 20, 0, 18, 154);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (67, 'Showkarte', 'Showkarte, mit der Sie eine Show in einem der vielen Theater in der Nähe besuchen können. Die Show ist von internationalen Künstlern, die verschiedene Genres darbieten, wie z.B. Comedy, Musical, Cabaret oder Erotik. Die Show findet jeden Abend um 20 Uhr statt. Sie erhalten eine Liste mit den aktuellen Programmen und Orten.', 30, 0, 18, 155);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (68, 'Limousinenservice', 'Bringt Sie zu jedem gewünschten Ort. Sie können aus verschiedenen Modellen und Farben wählen und einen Chauffeur mit Uniform und Handschuhen haben. Der Limousinenservice ist rund um die Uhr verfügbar und kann an der Rezeption des Hauses bestellt werden.', 50, 0, 18, 156);
INSERT INTO vacation_rental_db.options (id, name, description, price, is_disabled, house_id, image_id) VALUES (69, 'Stadtführung', 'Lassen Sie sich die Geschichte und die Highlights der Reeperbahn und Hamburg zeigen. Ein erfahrener Stadtführer zeigt Ihnen die verschiedenen Orte und erzählt Ihnen spannende Geschichten. Die Stadtführung dauert etwa 2 Stunden und startet jeden Tag um 14 Uhr vor dem Haus. Sie erhalten eine Broschüre mit den wichtigsten Informationen.', 15, 0, 18, 157);
